import React from 'react'


const Header = (props) =>{
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  );
};

const Part = (props) =>{
  return (
    <div>
      <p>{props.partNum} {props.exercisesNum}</p>
    </div>
  );
}

const Content = (props) =>{
  return (
    <div>
      <Part partNum={props.part1.name} exercisesNum={props.part1.exercises}></Part>
      <Part partNum={props.part2.name} exercisesNum={props.part2.exercises}></Part>
      <Part partNum={props.part3.name} exercisesNum={props.part3.exercises}></Part>
    </div>
  );
}

const Total = (props) =>{
  console.log(props);
  console.log(props.part1.exercises);
  console.log(props.part2.exercises);
  console.log(props.part1.exercises + props.part2.exercises);
  return (
    <div>
        <p>Number of exercises {props.part1.exercises} + {props.part2.exercises} + {props.part3.exercise}</p>
    </div>
  );
}



const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course}></Header>
      <Content part1={part1} part2={part2} part3={part3}></Content>
      <Total part1={part1} part2={part2} part3={part3}></Total>
    </div>
  )
}

export default App
