import React from 'react'


const Header = (props) =>{
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  );
};

const Part = (props) =>{
  return (
    <div>
      <p>{props.partNum} {props.exercisesNum}</p>
    </div>
  );
}

const Content = (props) =>{
  return (
    <div>
      <Part partNum={props.part[0].name} exercisesNum={props.part[0].exercises}></Part>
      <Part partNum={props.part[1].name} exercisesNum={props.part[1].exercises}></Part>
      <Part partNum={props.part[2].name} exercisesNum={props.part[2].exercises}></Part>
    </div>
  );
}

const Total = (props) =>{
  const numAllParts = props.part[0].exercises + props.part[1].exercises + props.part[2].exercises;
  return (
    <div>
        <p>Number of exercises {numAllParts}</p>
    </div>
  );
}



const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course}></Header>
      <Content part={parts}></Content>
      <Total part={parts}></Total>
    </div>
  )
}

export default App
